INSERT INTO  M4RCH_VT_TPL_OV (ID_ORGANIZATION ,ID_OBJECT_VALUES ,ID_TEMPLATE ,ID_ORGANIZATION_TV ,ID_DMD ,ID_DMD_COMPONENT ,IS_DELETE ,ID_APPROLE ,ID_SECUSER ,DT_LAST_UPDATE ) 
SELECT DISTINCT A.ID_ORGANIZATION ,A.ID_OBJECT_VALUES ,A.ID_TEMPLATE ,A.ID_ORGANIZATION_TV ,A.ID_DMD ,'#ID_DMD_COMPONENT#' ,1 ,'M4CONFIG','MHUM' ,GETDATE() FROM M4RCH_VT_TPL_OV A
WHERE NOT EXISTS (SELECT * FROM M4RCH_VT_TPL_OV B WHERE  A.ID_ORGANIZATION=B.ID_ORGANIZATION AND A.ID_OBJECT_VALUES=B.ID_OBJECT_VALUES AND A.ID_TEMPLATE=B.ID_TEMPLATE AND A.ID_ORGANIZATION_TV=B.ID_ORGANIZATION_TV AND A.ID_DMD=B.ID_DMD AND B.ID_DMD_COMPONENT='#ID_DMD_COMPONENT#')
